glsl4.vert:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
#if USE_LOCAL
  layout(set = 2, binding = 0) uniform CCLocal {
    highp mat4 cc_matWorld;
    highp mat4 cc_matWorldIT;
    highp vec4 cc_lightingMapUVParam;
  };
#endif
#if SAMPLE_FROM_RT
#endif
layout(location = 0) in vec3 a_position;
layout(location = 1) in vec2 a_texCoord;
layout(location = 2) in vec4 a_color;
layout(location = 0) out vec4 color;
layout(location = 1) out vec2 uv0;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  #if USE_LOCAL
  pos = cc_matWorld * pos;
  #endif
  #if USE_PIXEL_ALIGNMENT
  pos = cc_matView * pos;
  pos.xyz = floor(pos.xyz);
  pos = cc_matProj * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  uv0 = a_texCoord;
  #if SAMPLE_FROM_RT
  uv0 = cc_cameraPos.w > 1.0 ? vec2(uv0.x, 1.0 - uv0.y) : uv0;
  #endif
  color = a_color;
  return pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
  precision highp float;
  #if USE_ALPHA_TEST
    layout(set = 1, binding = 0) uniform ALPHA_TEST_DATA {
      float alphaThreshold;
    };
  #endif
  layout(set = 0, binding = 0) uniform CCGlobal {
    highp   vec4 cc_time;
    mediump vec4 cc_screenSize;
    mediump vec4 cc_nativeSize;
  };
  layout(set = 0, binding = 1) uniform CCCamera {
    highp   mat4 cc_matView;
    highp   mat4 cc_matViewInv;
    highp   mat4 cc_matProj;
    highp   mat4 cc_matProjInv;
    highp   mat4 cc_matViewProj;
    highp   mat4 cc_matViewProjInv;
    highp   vec4 cc_cameraPos;
    mediump vec4 cc_screenScale;
    mediump vec4 cc_exposure;
    mediump vec4 cc_mainLitDir;
    mediump vec4 cc_mainLitColor;
    mediump vec4 cc_ambientSky;
    mediump vec4 cc_ambientGround;
    mediump vec4 cc_fogColor;
    mediump vec4 cc_fogBase;
    mediump vec4 cc_fogAdd;
    mediump vec4 cc_nearFar;
    mediump vec4 cc_viewPort;
  };
  layout(location = 0) in vec4 color;
  #if USE_TEXTURE
    layout(location = 1) in vec2 uv0;
    layout(set = 2, binding = 10) uniform sampler2D cc_spriteTexture;
  #endif
  layout(set = 1, binding = 1) uniform Uniforms {
      vec4 iMouse;
  vec2 iResolution;
    };
  vec3 palette(float d){
  return mix(vec3(0.2,0.7,0.9),vec3(1.,0.,1.),d);
}
vec2 rotate(vec2 p,float a){
  float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}
float map(vec3 p){
    for( int i = 0; i<8; ++i){
        float t = cc_time[0]*0.2;
        p.xz =rotate(p.xz,t);
        p.xy =rotate(p.xy,t*1.89);
        p.xz = abs(p.xz);
        p.xz-=.5;
  }
  return dot(sign(p),p)/5.;
}
vec4 rm (vec3 ro, vec3 rd){
    float t = 0.;
    vec3 col = vec3(0.);
    float d;
    for(float i =0.; i<64.; i++){
    vec3 p = ro + rd*t;
        d = map(p)*.5;
        if(d<0.02){
            break;
        }
        if(d>100.){
          break;
        }
        col+=palette(length(p)*.1)/(400.*(d));
        t+=d;
    }
    return vec4(col,1./(d*100.));
}
vec4 frag() {
  vec4 ssr_frag_out;
    vec2 uv = (gl_FragCoord.xy-(iResolution.xy/2.))/iResolution.x;
  vec3 ro = vec3(0.,0.,-50.);
    ro.xz = rotate(ro.xz,cc_time[0]);
    vec3 cf = normalize(-ro);
    vec3 cs = normalize(cross(cf,vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cf,cs));
    vec3 uuv = ro+cf*3. + uv.x*cs + uv.y*cu;
    vec3 rd = normalize(uuv-ro);
    vec4 col = rm(ro,rd);
    ssr_frag_out = col;
    return ssr_frag_out;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

